mapper.select:
select o.*,(select sum(p.proj_num) from produce_plan p where o.order_sn = p.order_no and o.tenant_id = p.tenant_id ) as proj_num, (select sum(ptv.store_num) from plan_track_view ptv where ptv.proj_code in (select proj_code from produce_plan p where o.order_sn = p.order_no and o.tenant_id = p.tenant_id) and ptv.tenant_id = o.tenant_id) as store_num from produce_order o -- select o.* -- from v_order_plan o WHERE 1=1
and o.order_sn like '%${param.orderSn}%'
and o.material_code like '%${param.materialCode}%'
and o.branch_code like '%${param.branchCode}%'
and o.delivery_date >= #{param.startTime}
and o.delivery_date <= #{param.endTime}
and o.status = '${param.status}'
and o.tenant_id like '${param.tenantId}'
and o.status != '2'
order by ${param.orderCol} ${param.order}
