mapper.select:
select `p`.`id` AS `id`,`p`.`proj_code` AS `proj_code`,`p`.`work_no` AS `work_no`,`p`.`order_id` AS `order_id`,`p`.`order_no` AS `order_no`,`p`.`order_delivery_date` AS `order_delivery_date`,`p`.`project_bom` AS `project_bom`,`p`.`project_bom_name` AS `project_bom_name`,`p`.`draw_no` AS `draw_no`,`p`.`proj_num` AS `proj_num`,`p`.`sort_no` AS `sort_no`,`p`.`start_time` AS `start_time`,`p`.`end_time` AS `end_time`,`p`.`priority` AS `priority`,`p`.`proj_type` AS `proj_type`,`p`.`branch_code` AS `branch_code`,`p`.`incharge_org` AS `incharge_org`,`p`.`status` AS `status`,`p`.`alarm_status` AS `alarm_status`,`p`.`create_by` AS `create_by`,`p`.`create_time` AS `create_time`,`p`.`modify_by` AS `modify_by`,`p`.`modify_time` AS `modify_time`,`p`.`remark` AS `remark`,`p`.`tenant_id` AS `tenant_id`,`p`.`draw_no_name` AS `draw_no_name`,`p`.`draw_no_type` AS `draw_no_type`,(select `produce_order`.`order_sn` from `produce_order` where (`produce_order`.`id` = `p`.`order_no`)) AS `order_code`,(select ifnull(sum(`produce_track_head`.`number`),0) from `produce_track_head` where ((`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`))) AS `track_num`,(select ifnull(sum(`produce_track_head`.`number`),0) from `produce_track_head` where ((`produce_track_head`.`status` = '1') and (`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`))) AS `process_num`,(select ifnull(sum(`produce_track_head`.`number`),0) from `produce_track_head` where ((`produce_track_head`.`status` = '2') and (`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`))) AS `store_num`,(select count(1) from `produce_track_item` where `produce_track_item`.`track_head_id` in (select `produce_track_head`.`id` from `produce_track_head` where ((`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`)))) AS `total_option`,(select ifnull(sum(`produce_track_item`.`complete_qty`),0) from `produce_track_item` where ((`produce_track_item`.`is_final_complete` = 1) and `produce_track_item`.`track_head_id` in (select `produce_track_head`.`id` from `produce_track_head` where ((`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`))))) AS `complete_option`,(select sum(cast(`produce_track_head`.`status` as signed)) from `produce_track_head` where ((`produce_track_head`.`work_plan_id` = `p`.`id`) and (`produce_track_head`.`tenant_id` = `p`.`tenant_id`) and (`produce_track_head`.`branch_code` = `p`.`branch_code`))) AS `real_status` from `produce_plan` `p` WHERE 1=1
and p.proj_code like '%${param.projCode}%'
and p.work_no like '%${param.workNo}%'
and p.order_no like '%${param.orderNo}%'
and p.draw_no like '%${param.drawNo}%'
and p.start_time >= #{param.startTime}
and p.start_time <= #{param.endTime}
and p.status = ${param.status}
and p.tenant_id = #{param.tenantId}
and p.branch_code = #{param.branchCode}
and p.status != 2
and proj_num > pt.track_num
order by ${param.orderCol} ${param.order}
