mapper.select:
select a.template_code templateCode,sum(a.number) number from produce_track_head a
and a.branch_code like "%"#{branchCode}"%"
and a.create_time >= #{createTime}
and a.create_time <= #{endTime}
GROUP BY a.template_code

SELECT c.template_code templateCode,sum(a.qty) qty FROM produce_assign a Left Join produce_track_item b on b.id=a.ti_id Left Join produce_track_head c on c.id=b.track_head_id
and c.branch_code like "%"#{branchCode}"%"
and a.assign_time >= #{createTime}
and a.assign_time <= #{endTime}
GROUP BY c.template_code

select c.template_code templateCode, sum(a.completed_qty) completed_qty FROM produce_track_complete a LEFT JOIN produce_track_item b ON b.id=a.ti_id LEFT JOIN produce_track_head c ON c.id=b.track_head_id
and c.branch_code like "%"#{branchCode}"%"
and a.complete_time >= #{createTime}
and a.complete_time <= #{endTime}
GROUP BY c.template_code

SELECT c.template_code templateCode,Sum(a.qualify+a.unqualify ) qualify FROM produce_track_check a LEFT JOIN produce_track_item b ON b.id=a.ti_id LEFT JOIN produce_track_head c ON c.id=b.track_head_id
and c.branch_code like "%"#{branchCode}"%"
and a.deal_time >= #{createTime}
and a.deal_time <= #{endTime}
GROUP BY c.template_code
